import React from 'react';
import { Route, Link, BrowserRouter as Router, Redirect, BrowserRouter, Switch } from 'react-router-dom'
import Dashbord from './pages/dashboard/dashboard';
import Login from './pages/login/login';
import Register from './pages/register/register';
import api from './api/api';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = { authed: false, loaded: false };
  }

  async componentWillMount() {
    const { data } = await api.get("user/validsession");
    this.setState({ authed: true, loaded: true });
  }


  render() {
    if (!this.state.loaded) {
      return (
        <div>
          <p>Loading...</p>
        </div>
      );
    }
    return (
      <div>
        <BrowserRouter>
          <Switch>
            <Route path="/login" component={Login} exact />
            <Route path="/register" component={Register} exact/>
            <PrivateRoute authed={this.state.authed} path="/" component={Dashbord} exact/>
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}

class PrivateRoute extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <Route {...this.props.rest} render={props => (
        this.props.authed
          ? <React.Component {...this.props.component} />
          : <Redirect to='/login' />
      )} />
    );
  }
}


export default App;
