import React from 'react';
import { Route, Link, BrowserRouter as Router, Redirect } from 'react-router-dom'
import Dashbord from './pages/dashboard/dashboard';
import Login from './pages/login/login';
import Register from './pages/register/register';
import api from './api/api';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {authed: false, loaded: false};
  }

  async componentWillMount() {
    const { data } = await api.get("user/validsession");
    this.setState({authed: false, loaded: true});
  }


  render() {
    if (!this.state.loaded) {
      return (
        <div>
          <p>Loading...</p>
        </div>
      );
    }
    return (
      <div>
        <Router>
          <Route path="/login" component={Login}/>
          <Route path="/register" component={Register}></Route>
          <PrivateRoute authed={this.state.authed} path="/" component={Dashbord}/>
        </Router>
      </div>
    );
  }
}

class PrivateRoute extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return(
      <Route {...this.props} render={(props) => (
        props.authed
          ? <React.Component {...props.component} />
          : <Redirect to='/login' />
      )} />
    );
  }
}


export default App;
